// Override _write to send characters to ITM Port 0
int _write(int file, char *ptr, int len) {
    int i;
    for (i = 0; i < len; i++) {
        // Check if ITM is enabled
        if (DEMCR & TRCENA) {
            // Wait until the port is ready to accept data
            while (ITM_Port32(0) == 0);
            // Write the character
            ITM_Port8(0) = ptr[i];
        }
    }
    return len;
}

